function colortag(){$("ul.tag-list li").each((function(){let a=Math.floor(5*Math.random()+1);2==a?$(this).find("a").addClass("tagred"):3==a?$(this).find("a").addClass("tagyellow"):4==a?$(this).find("a").addClass("tagblue"):5==a?$(this).find("a").addClass("taggreen"):2==a?$(this).find("a").addClass("tagpurple"):1==a&&$(this).find("a").addClass("taggrown")}))}colortag();